package lt.lhu.unit8.main;

public class task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x = 0.5;
		double y;
		
		y = Math.sqrt(Math.pow(Math.E, 2.2 * x)) - Math.abs(Math.sin(Math.PI * x / (x + 2.0 / 3.0))) + 1.7;
		System.out.print(y);
	}

}
